function y=mvnbnvg(x,nptsb,nptsmv)% function y=mvnbnvg(x,nptsb,nptsmv)% mm=0;% [m,n]=size(x);% if m==1,x=x';[m,n]=size(x);mm=1;end% nbins=floor((m-nptsb)/nptsmv)+1;% y=zeros(nbins,n);% % ifrst=0;%  for ibin=1:nbins%        %        y(ibin,1:n)=sum(x((ibin-1)*nptsmv+1:(ibin-1)*nptsmv+nptsb,1:n))/nptsb;%         %   end% if mm==1,y=y';endmm=0;[m,n]=size(x);if m==1,x=x';[m,n]=size(x);mm=1;endnbins=floor((m-nptsb)/nptsmv)+1;y=zeros(nbins,n);ifrst=0; for ibin=1:nbins              y(ibin,1:n)=sum(x((ibin-1)*nptsmv+1:(ibin-1)*nptsmv+nptsb,1:n))/nptsb;          endif mm==1,y=y';end